def calc_hash(apiname, hval_init):
    # calculate the Fowler-Noll-Vo hash based on apiname and initial hash value which changes for every API
    FNV_PRIME = 0x1000193
    FNV_SIZE = 2**32
    return fnvhash.fnva(apiname, hval_init, FNV_PRIME, FNV_SIZE)

def find_api_for_hash(req_hash, hval_init):
    # find the API name that belongs to the requested hash and the FNV initial hash value
    api_lists = {'kernel32.dll' : api_list_kernel32}
    for dllname, api_list in api_lists.items():
        for apiname in api_list:
            ahash = calc_hash(apiname.encode('utf-8'), hval_init)
            if ahash == req_hash:
                return dllname + '.' + apiname
    return None

def find_api_and_put_comment(ea, vinit):
    # looking for instructions of the form: cmp esi, <api_hash> by looking forward 100 heads
    # then extracting the api hash and setting a comment in the decompiler
    REG_NAME = 'esi'
    REG_SIZE = 4
    INST_NAME = 'cmp'
    MAX_LOOKAHEAD = 100
    e_count = 0
    ## count foward 100 heads
    while e_count < MAX_LOOKAHEAD:
        try:
            e_count += 1
            ea = idc.next_head(ea)
            # check for correct instruction
            if idc.print_insn_mnem(ea) != INST_NAME: 
                continue
            opval = idc.get_operand_value(ea, 0)
            tmp_reg_name = idaapi.get_reg_name(opval, REG_SIZE)
            # check for correct operands and operand types
            if not tmp_reg_name or REG_NAME.lower() != tmp_reg_name.lower() or idc.get_operand_type(ea, 1) != o_imm: 
                continue
            apihash = idc.get_operand_value(ea, 1) & 0xFFFFFFFF
            if not apihash: 
                continue
            api_found = find_api_for_hash(apihash, vinit)
            if not api_found: 
                continue
            set_comment(ea, api_found)
            return api_found
        except OverflowError: 
            continue
    return None
    
def find_fnvinit(ea):
    # looking for instructions of the form: mov esi, <fnv_init_value> by going back 100 heads
    REG_NAME = 'esi'
    REG_SIZE = 4
    INST_NAME = 'mov'
    MAX_LOOKBACK = 100
    e_count = 0
    ## count back 100 heads
    while e_count < MAX_LOOKBACK:
        try:
            e_count += 1
            ea = idc.prev_head(ea)
            if idc.print_insn_mnem(ea) != INST_NAME:
                continue
            opval = idc.get_operand_value(ea, 0)
            tmp_reg_name = idaapi.get_reg_name(opval, REG_SIZE)
            if tmp_reg_name and REG_NAME.lower() == tmp_reg_name.lower() and idc.get_operand_type(ea, 1) == o_imm:
                return idc.get_operand_value(ea, 1) & 0xFFFFFFFF
        except OverflowError as e: 
            # for the wrong instructions get_reg_name causes an OverflowError
            continue
    return None

def is_fnvprime_instruction(ea):
    # looking for instructions of the form: imul esi, ecx, 1000193h
    FNV_PRIME = 0x1000193
    mnemonic = idc.print_insn_mnem(ea)
    REG_SIZE = 4
    if mnemonic == "imul":
        reg_name = idaapi.get_reg_name(idc.get_operand_value(ea, 0), REG_SIZE)
        if not reg_name: return False
        elif "esi".lower() == reg_name.lower():
            try:
                reg_name = idaapi.get_reg_name(idc.get_operand_value(ea, 1), REG_SIZE)
                if not reg_name: return False
                if "ecx".lower() == reg_name.lower() and\
                    idc.get_operand_type(ea, 2) == o_imm and\
                    idc.get_operand_value(ea, 2) == FNV_PRIME:
                        return True
            except OverflowError as e: return False
    return False
    
def set_comment(address, comment):
    # set comment in disassembler and decompiler at specified address
    idc.set_cmt(address, comment, 0)
    cfunc = idaapi.decompile(address)
    if not cfunc: return
    eamap = cfunc.get_eamap()
    decompObjAddr = eamap[address][0].ea

    tl = idaapi.treeloc_t()
    tl.ea = decompObjAddr
    commentSet = False

    for itp in range(idaapi.ITP_SEMI, idaapi.ITP_COLON):
        tl.itp = itp
        cfunc.set_user_cmt(tl, comment)
        cfunc.save_user_cmts()
        unused = cfunc.__str__()
        if not cfunc.has_orphan_cmts():
            commentSet = True
            cfunc.save_user_cmts()
            break
        cfunc.del_orphan_cmts()

def find_fnvprime_instructions():
    # find all instruction addresses where the instructions is: imul esi, ecx, 1000193h
    # this serves to identify Fowler-Noll-Vo algorithm
    start = idaapi.get_inf_structure().min_ea
    end = idaapi.get_inf_structure().max_ea
    instr_list = [ea for ea in range(start,end) if idaapi.is_code(get_full_flags(ea)) and is_fnvprime_instruction(ea)]
    return instr_list

if __name__ == "__main__":
    print('start')
    # identify FNV algorithm usage in the code
    instr = find_fnvprime_instructions()
    for ea in instr: 
        print("Found target instruction at: 0x%X" % ea)
        # for each FNV determine the initial hash value, it is different every time
        # probably to prevent HashDB from working
        vinit = find_fnvinit(ea)
        if not vinit: 
            continue
        print('vinit 0x%X' % vinit)
        # now find the API hash and figure out the API name that belongs to it
        # then put a comment at the address that contains the API hash
        api = find_api_and_put_comment(ea, vinit)
        print('api', api)
    print('done')