size64 = 0x2E00
size32 = 0x2C00

def main(filepath, arch):
	outpath = filepath + '.pe.bin'
	pe_data = None
	with open(filepath, 'rb') as f:
		data = bytearray(f.read())
		if arch == 'win32':
			pe_data = stub32
			pe_data.extend(data)
			while len(pe_data) < size32:
				pe_data.extend(b'\00')
		else:
			pe_data = stub64
			pe_data.extend(data)
			while len(pe_data) < size64:
				pe_data.extend(b'\00')
		with open(outpath, 'wb') as o:
			o.write(pe_data)
			print('successfully created PE file', outpath)
	print('done')
			

if __name__ == '__main__':
	parser = argparse.ArgumentParser(description='Shellcode to PE converter')
	parser.add_argument('arch', help='architecture, win32 or win64')
	parser.add_argument('shellcode', help='shellcode file path')
	
	args = parser.parse_args()
	if args.arch != 'win32' and args.arch != 'win64':
		print('not supported architecture', args.arch)
	else:
		main(args.shellcode, args.arch)
	